import React, { Component } from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';


const Button = styled.div `
    background-image: linear-gradient(to top right, #8262b8 , #23778F);
    border-radius: 20px;
    width: 500px;
    height 150px;
    box-shadow: 30px 30px 5px 0px rgba(255,192,203 ,1 );
    font-size: 30px;
    font-color: white;
    transition: all 0.5s;

    &:hover{
      transition: 0.5s;
      box-shadow: 0px 0px 0px 0px rgba(255,192,203 ,1 );
      background-image: linear-gradient(to top right, #23778F, #8262b8);
    }
    ${props => props.inverted && `
      background-image: linear-gradient(to top right, #23778F, #8262b8);
      `}
`


const TestButton = (props) => {
  return(
    <div>
      <Button inverted={props.inverted}> Button </Button>
    </div>
  )
}

Button.propTypes = {
  inverted: PropTypes.bool
}
export default Button;
