import React, { Component } from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';


const Button = styled.div `
    background-color: white;
    border: 2px solid black;
    border-radius: 10px;
    padding-top: 1.2px;
    font-weight: 500px;
    width: 80px;
    height 20px;
    font-size: 16px;
    font-color: white;
    transition: all 0.5s;

    &:hover{
      transition: 0.5s;
      box-shadow: 0px 0px 30px 3px rgba(100,100,100 ,1 );
      background-image: linear-gradient(to top right, #fffff, #00000);
    }
`


const TestButton = (props) => {
  return(
    <div>
      <Button> inverted={props.inverted} </Button>
    </div>
  )
}

Button.propTypes = {
  inverted: PropTypes.bool
}
export default Button;
