import PropTypes from 'prop-types'
import './Products.css';
import Button from '../Button'
class Products extends Component {
  state = {
      products: [],
      cart: ""
    }

    componentDidMount() {
      this.fetchApi();
      this.fetchCart();
    }

    fetchApi = () => {
      const api = 'http://localhost:28071/api/items';

      fetch(api)
      .then(res => res.json())
      .then(item =>{
        // data.map(item => {
        this.setState({
          products: item
        })
      // })
      })
    }

    fetchCart = () => {
      const cart = 'http://localhost:28071/api/myGuid';

      fetch(cart)
      .then(res => res.json())
      .then(cart =>{
        // data.map(item => {
        this.setState({
          cart: cart
        })
      // })
      })

    }

    render() {
      console.log(this.state.products)
      console.log(this.state.cart)

      var myGuid = localStorage.getItem("myGuid")

      console.log(myGuid)
      if(myGuid == null){

      }
      else{
        localStorage.setItem("myGuid", this.state.cart);
      }

      // const names = this.state.products.name
      // const names = this.state.products.name
      // const price = this.state.products.price
      // console.log(names);
      // console.log(price);
      return (
        <div className="ProductsContainer">
          {this.state.products.map((item, key) =>
            <div className="ProductBox" key={key}>
              <h2>{item.name} </h2>
              <p>{item.price}kr </p>
              <p className="description">{item.description}kr </p>
              <img src={item.img_url} />
              <div className="Button">
